import React, { useState } from 'react';
import TaskForm from './TaskForm';
import TaskList from './TaskList';

//  TODO Поставить запрет на добавление 
//  новых тасков пока идёт редактирование

function Todo() {
  const [tasks, setTasks] = useState([]);

  const handleAddTask = (content) => {
    setTasks([
      { id: Date.now(), text: content, isCompleted: false, isEdit: false },
      ...tasks,
    ]);
    console.log(tasks);
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleEditTodo = (id, newContent) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, text: newContent } : task
      )
    );
  };

  const handleToggleTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  return (
    <div
      style={{
        margin: ' 0 auto',
        padding: '30px 10px',
        maxWidth: '800px',
      }}
    >
      <h1
        style={{
          color: '#E99787',
          textAlign: 'center',
          paddingBottom: '30px',
        }}
      >
        Todo List
      </h1>
      <TaskForm handleAddTask={handleAddTask} />
      <TaskList
        tasks={tasks}
        handleDeleteTask={handleDeleteTask}
        handleToggleTask={handleToggleTask}
        handleEditTodo={handleEditTodo}
      />
    </div>
  );
}

export default Todo;
