import React, { useState } from 'react';
import { Input, Button, List, Checkbox } from 'antd';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const handleAddTask = () => {
    setTasks([...tasks, { content: task, id: Date.now(), completed: false }]);
    setTask('');
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleToggleTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  return (
    <div>
      <h1>Todo List</h1>

      <Input
        value={task}
        onChange={handleTaskChange}
        placeholder="Enter a task"
        style={{ marginBottom: '16px' }}
      />

      <Button type="primary" onClick={handleAddTask} style={{ marginLeft: '16px' }}>
        Add Task
      </Button>

      <List
        dataSource={tasks}
        renderItem={(item) => (
          <List.Item
            actions={[
              <Checkbox
                checked={item.completed}
                onChange={() => handleToggleTask(item.id)}
              />,
              <Button onClick={() => handleDeleteTask(item.id)} danger>Delete</Button>,
            ]}
            style={{
              textDecoration: item.completed ? 'line-through' : 'none',
            }}
          >
            {item.content}
          </List.Item>
        )}
        style={{ marginTop: '16px' }}
      />
    </div>
  );
}

export default App;
