import React from 'react';
import { Button, List, Checkbox } from 'antd';

const TaskItem = ({ task, handleDeleteTask, handleToggleTask }) => {
  return (
    <List.Item
      actions={[
        <Checkbox checked={task.completed} onChange={() => handleToggleTask(task.id)} />,
        <Button danger textHoverBg ='rgba(1, 0, 0, 1)' onClick={() => handleDeleteTask(task.id)}>
          Delete
        </Button>,
      ]}
      style={{
        textDecoration: task.completed ? 'line-through' : 'none',
      }}
    >
      {task.content}
    </List.Item>
  );
};

export default TaskItem;
