import React from 'react';
import { List } from 'antd';
import TaskItem from './TaskItem';
import './TaskList.css';

const TaskList = ({ tasks, handleDeleteTask, handleToggleTask }) => {
  return (
    
    <List className='list'
      locale={{ emptyText: 'No tasks' }}
      pagination={tasks.length > 7 ? {
        pageSize: 7,
        align: 'center',
        }: false}
      align = {'center'}
      dataSource= {tasks}
      itemPadding = {'12px 100px'}
      renderItem={(task) => (
        <TaskItem
          task={task}
          handleDeleteTask={handleDeleteTask}
          handleToggleTask={handleToggleTask}
        />
      )}
    />
  );
};



export default TaskList;
