import React from 'react';
import { Button, List, Checkbox } from 'antd';

const TaskItem = ({ task, handleDeleteTask, handleToggleTask }) => {
  return (
    <List.Item className='list-item'
      actions={[
        <Checkbox checked={task.completed} onChange={() => handleToggleTask(task.id)} />,
        <Button danger onClick={() => handleDeleteTask(task.id)}>
          Delete
        </Button>,
      ]}
      style={{
        textDecoration: task.completed ? 'line-through' : 'none',
        color: '#C9D1C8',
        padding: '10px',
        borderRadius: '5px',
        backgroundColor: task.completed ? 'rgba(0, 230, 0, 0.1)' : 'rgba(0, 0, 0, 0.5)',
        fontSize:'16px', 
      }}
    >
      {task.content}
    </List.Item>
  );
};

export default TaskItem;
