import React from 'react';
import { List } from 'antd';
import TaskItem from './TaskItem';
import './TaskList.css';

const TaskList = ({ tasks, handleDeleteTask, handleToggleTask }) => {
  return (
    <List className='list'
      contentWidth= {400}
      locale={{ emptyText: 'No tasks' }}
      pagination={{
        pageSize: 5,
        align: 'center',
      }}
      align = {'center'}
      dataSource= {tasks}
      renderItem={(task) => (
        <TaskItem
          task={task}
          handleDeleteTask={handleDeleteTask}
          handleToggleTask={handleToggleTask}
        />
      )}
    />
  );
};

export default TaskList;
