import React, { useState } from 'react';
import { Button, Input, List, Checkbox } from 'antd';

const TaskItem = ({
  task,
  handleDeleteTask,
  handleEditTodo,
  handleToggleTask,
}) => {
  const [edit, setEdit] = useState();

  const handleTaskChange = (e) => {
    setEdit(e.target.value);
  };

  const handleSubmit = () => {
    setEdit(task.text);
    if (task && task.edit) {
      handleEditTodo(task.id, edit);
    }
  };

  return (
    <List.Item
      actions={[
        <Checkbox
          checked={task.completed}
          onChange={() => {
            if (!task.edit) {
              handleToggleTask(task.id);
            }
          }}
        />,
        <Button
          onClick={ handleSubmit
          //   () => { 
          //   // setEdit(task.text);
          //   // if (!task.completed || task.edit) {
          //   //   handleEditTodo(task.id, edit);
          //   // }
          // }
        }
          style={{
            color: '#727077',
            backgroundColor: 'rgba(0, 0, 0, 0.05)',
            borderColor: 'rgba(0, 0, 0, 0.3)',
          }}
        >
          {!task.completed && task.edit ? 'Save' : 'Edit'}
        </Button>,
        <Button
          danger
          onClick={() => handleDeleteTask(task.id)}
          style={{
            backgroundColor: 'rgba(250, 0, 0, 0.2)',
            borderColor: 'rgba(250, 0, 0, 0.3)',
          }}
        >
          Delete
        </Button>,
      ]}
      style={{
        textAlign: 'left',
        wordBreak: 'break-word',
        marginBottom: '15px',
        textDecoration: task.completed ? 'line-through' : 'none',
        color: task.completed ? '#727077' : '#04202C',
        padding: '10px',
        borderRadius: '5px',
        backgroundColor: task.completed
          ? 'rgba(0, 220, 0, 0.15)'
          : 'rgba(233, 151, 135, 0.5)',
        fontSize: '16px',
      }}
    >
      {!task.completed && task.edit ? (
        <Input value={edit} onChange={handleTaskChange}></Input>
      ) : (
        task.text
      )}
    </List.Item>
  );
};

export default TaskItem;
