import React from 'react';
import { Button, List, Checkbox, ConfigProvider } from 'antd';
import './TaskItem.css';


const TaskItem = ({ task, handleDeleteTask, handleToggleTask }) => {
  <ConfigProvider
    theme={{
      token: {
        // Seed Token
        colorPrimary: '#00b96b',
        borderRadius: 2,

        // Alias Token
        colorBgContainer: '#f6ffed',
      },
    }}
  ></ConfigProvider>
  return (
    <List.Item className='list-item'
      actions={[
        <Checkbox checked={task.completed} onChange={() => handleToggleTask(task.id)} />,
        <Button danger onClick={() => handleDeleteTask(task.id)}>
          Delete
        </Button>,
      ]}
      style={{
        textDecoration: task.completed ? 'line-through' : 'none',
      }}
    >
      {task.content}
    </List.Item>
  );
};

export default TaskItem;
