import React, { useState } from 'react';
import { Input, Button, Checkbox } from 'antd';
import 'antd/dist/antd.css';

const App = () => {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editingTodoId, setEditingTodoId] = useState(null);
  const [editingTodoText, setEditingTodoText] = useState('');

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      const newTodo = {
        id: Date.now(),
        text: inputValue,
        completed: false,
      };
      setTodos([...todos, newTodo]);
      setInputValue('');
    }
  };

  const handleEditTodo = (id, newText) => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        return {
          ...todo,
          text: newText,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
    setEditingTodoId(null);
    setEditingTodoText('');
  };

  const handleToggleCompleted = (id) => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        return {
          ...todo,
          completed: !todo.completed,
        };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleDeleteTodo = (id) => {
    const updatedTodos = todos.filter(todo => todo.id !== id);
    setTodos(updatedTodos);
  };

  const handleStartEditing = (id, text) => {
    setEditingTodoId(id);
    setEditingTodoText(text);
  };

  const handleCancelEditing = () => {
    setEditingTodoId(null);
    setEditingTodoText('');
  };

  const handleSaveEditing = (id, newText) => {
    if (newText.trim() !== '') {
      handleEditTodo(id, newText);
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '0 auto' }}>
      <h1>TODO List</h1>
      <Input
        placeholder="Enter a task"
        value={inputValue}
        onChange={e => setInputValue(e.target.value)}
        style={{ marginBottom: 16 }}
      />
      <Button type="primary" onClick={handleAddTodo}>
        Add Task
      </Button>
      {todos.map(todo => (
        <div key={todo.id} style={{ marginTop: 16 }}>
          <Checkbox
            checked={todo.completed}
            onChange={() => handleToggleCompleted(todo.id)}
            style={{ marginRight: 8 }}
          />
          {editingTodoId === todo.id ? (
            <Input
              autoFocus
              value={editingTodoText}
              onChange={e => setEditingTodoText(e.target.value)}
              onBlur={() => handleSaveEditing(todo.id, editingTodoText)}
              onKeyDown={e => {
                if (e.key === 'Enter') {
                  handleSaveEditing(todo.id, editingTodoText);
                } else if (e.key === 'Escape') {
                  handleCancelEditing();
                }
              }}
            />
          ) : (
            <>
              <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
                {todo.text}
              </span>
              <Button type="link" onClick={() => handleStartEditing(todo.id, todo.text)}>
                Edit
              </Button>
              <Button type="link" onClick={() => handleDeleteTodo(todo.id)}>
                Delete
              </Button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default App;
