import React from 'react';
import { Button, List, Checkbox } from 'antd';

const TaskItem = ({ task, handleDeleteTask, handleToggleTask }) => {
  return (
    <List.Item
      className="list-item"
      actions={[
        <Checkbox
          checked={task.completed}
          onChange={() => handleToggleTask(task.id)}
        />,
        <Button
          danger
          onClick={() => handleDeleteTask(task.id)}
          style={{
            backgroundColor: 'rgba(250, 0, 0, 0.2)',
            borderColor: 'rgba(250, 0, 0, 0.3)',
          }}
        >
          Delete
        </Button>,
      ]}
      style={{
        paddingBottom: '15px 0',
        textDecoration: task.completed ? 'line-through' : 'none',
        color: task.completed ? '#04202C' : '#C9D1C8',
        padding: '10px',
        borderRadius: '5px',
        backgroundColor: task.completed
          ? 'rgba(0, 220, 0, 0.05)'
          : 'rgba(0, 0, 0, 0.5)',
        fontSize: '16px',
      }}
    >
      {task.content}
    </List.Item>
  );
};

export default TaskItem;
