import React, { useState } from 'react';
import { Input, Button, List } from 'antd';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const handleAddTask = () => {
    setTasks([...tasks, { content: task, id: Date.now() }]);
    setTask('');
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div>
      <h1>Todo List</h1>

      <Input
        value={task}
        onChange={handleTaskChange}
        placeholder="Enter a task"
        style={{ marginBottom: '16px' }}
      />

      <Button type="primary" onClick={handleAddTask} style={{ marginLeft: '16px' }}>
        Add Task
      </Button>

      <List
        dataSource={tasks}
        renderItem={(item) => (
          <List.Item actions={[<Button onClick={() => handleDeleteTask(item.id)} danger>Delete</Button>]}>
            {item.content}
          </List.Item>
        )}
        style={{ marginTop: '16px' }}
      />
    </div>
  );
}

export default App;
